import React from 'react';
import { post } from 'axios';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    hidden: {
        display:'none'
    }

});


class CustomerAdd extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            file: null,
            username: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        }
    }

    handleFormSubmit = (e) => {
        e.preventDefault();
        this.addCustomer()
            .then((response) => {
                console.log(response.data);
                this.props.stateRefresh();
            })
        this.setState({
            file: null,
            username: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: ''
        })
    }

    handleFileSubmit = (e) => {
        this.setState({
            file: e.target.files[0],
            fileName: e.target.value
        })
    }

    handleValueSubmit = (e) => {
        let nextStage = {};
        nextStage[e.target.name] = e.target.value;
        this.setState(nextStage);
    }

    addCustomer = () => {
        const url = '/api/customers';
        const formData = new FormData();
        formData.append('image', this.state.file)
        formData.append('name', this.state.username)
        formData.append('birthday', this.state.birthday)
        formData.append('gender', this.state.gender)
        formData.append('job', this.state.job)
        const config = {
            headers: {
                'context-type': 'multipart/form-data'
            }
        }
        return post(url, formData, config)
    }

    handleClickOpen = () => {
        this.setState({
            open: true
        })
    }

    handleClickClose = () => {
        this.setState({
            file: null,
            username: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        })
    }

    render() {
        // Class Props Initialized.
        const { classes } = this.props;


        return (
            <div>
                <Button variant='contained' color='primary' onClick={this.handleClickOpen}>
                    Add
                </Button>
                <Dialog open={this.state.open} onClick={this.handleClickClose}>
                    <DialogTitle>
                        Add Information
                    </DialogTitle>
                    <DialogContent>
                        <input className={classes.hidden} accept='image/*'
                                    id='raised-button-file'type="file" 
                                    file={this.state.file} 
                                    value={this.state.fileName}
                                    onChange={this.handleFileSubmit} /><br/>
                        <label htmlFor='rasied-button-file'>
                            <button variant='contained' color='primary' component='span' name='file'>
                                {this.state.fileName == '' ? 'Select Profile Image':this.state.fileName}
                            </button>
                        </label><br/>
                        <TextField label="Name" type="text" name="username" value={this.state.username} onChange={this.handleValueSubmit} /><br/>
                        <TextField label="DOB" type="text" name="birthday"
                                    value={this.state.birthday}
                                    onChange={this.handleValueSubmit} /><br/>
                        <TextField label="Gender" type="text" name="gender"
                                    value={this.state.gender}
                                    onChange={this.handleValueSubmit} /><br/>
                        <TextField label="Job" type="text" name="job"
                                    value={this.state.job}
                                    onChange={this.handleValueSubmit} /><br/>
                    </DialogContent>
                </Dialog>
            </div>
        )
    }

}

export default withStyles(styles)(CustomerAdd);
